'use strict!'
$(document).ready(function() {
  $('#pcoded').pcodedmenu({
    themelayout: 'vertical',
    verticalMenuplacement: 'left',
    verticalMenulayout: 'wide',
    MenuTrigger: 'click',
    SubMenuTrigger: 'click',
    activeMenuClass: 'active',
    ThemeBackgroundPattern: 'pattern6',
    HeaderBackground: 'theme5',
    LHeaderBackground: 'theme1',
    NavbarBackground: 'themelight1',
    ActiveItemBackground: 'theme4',
    SubItemBackground: 'theme2',
    ActiveItemStyle: 'style0',
    ItemBorder: true,
    ItemBorderStyle: 'none',
    NavbarImage: 'false',
    ActiveNavbarImage: 'img1',
    SubItemBorder: true,
    DropDownIconStyle: 'style3',
    menutype: 'st2',
    layouttype: 'light',
    FixedNavbarPosition: true,
    FixedHeaderPosition: true,
    collapseVerticalLeftHeader: true,
    VerticalSubMenuItemIconStyle: 'style7',
    VerticalNavigationView: 'view1',
    verticalMenueffect: { desktop: 'shrink', tablet: 'overlay', phone: 'overlay' },
    defaultVerticalMenu: { desktop: 'expanded', tablet: 'offcanvas', phone: 'offcanvas' },
    onToggleVerticalMenu: { desktop: 'offcanvas', tablet: 'expanded', phone: 'expanded' }
  })
  function c() {
    $('.theme-color > a.navbg-pattern').on('click', function() {
      var w = $(this).attr('navbg-pattern')
      $('.pcoded').attr('sidebar-img-type', w)
    })
  }
  c()
  function b() {
    $('.theme-color > a.Layout-type').on('click', function() {
      var w = $(this).attr('layout-type')
      $('.pcoded').attr('layout-type', w)
      if (w == 'dark') {
        $('.pcoded-header').attr('header-theme', 'theme1')
        $('.pcoded-navbar').attr('navbar-theme', 'theme1')
        $('.pcoded').attr('sidebar-img', 'false')
        $('body').addClass('dark')
        $('.pcoded-navigation-label').attr('menu-title-theme', 'theme6')
      }
      if (w == 'light') {
        $('.pcoded-header').attr('header-theme', 'theme5')
        $('.pcoded-navbar').attr('navbar-theme', 'themelight1')
        $('.pcoded').attr('sidebar-img', 'false')
        $('.pcoded-navigation-label').attr('menu-title-theme', 'theme1')
        $('body').removeClass('dark')
      }
      if (w == 'img') {
        $('.pcoded-header').attr('header-theme', 'theme1')
        $('.pcoded-navbar').attr('navbar-theme', 'theme1')
        $('.navbar-logo').attr('logo-theme', 'theme1')
        $('.pcoded').attr('sidebar-img', 'true')
        $('.pcoded-navigation-label').attr('menu-title-theme', 'theme6')
      }
    })
  }
  b()
  function o() {
    $('.theme-color > a.logo-theme').on('click', function() {
      var w = $(this).attr('logo-theme')
      $('.navbar-logo').attr('logo-theme', w)
    })
  }
  o()
  function p() {
    $('.theme-color > a.leftheader-theme').on('click', function() {
      var w = $(this).attr('lheader-theme')
      $('.pcoded-navigation-label').attr('menu-title-theme', w)
    })
  }
  p()
  function e() {
    $('.theme-color > a.header-theme').on('click', function() {
      var w = $(this).attr('header-theme')
      $('.pcoded-header').attr('header-theme', w)
      $('.navbar-logo').attr('logo-theme', w)
    })
  }
  e()
  function m() {
    $('.theme-color > a.navbar-theme').on('click', function() {
      var w = $(this).attr('navbar-theme')
      $('.pcoded-navbar').attr('navbar-theme', w)
      $('.pcoded').attr('sidebar-img', 'false')
      if (w == 'themelight1') {
        $('.pcoded-navigation-label').attr('menu-title-theme', 'theme1')
      }
      if (w == 'theme1') {
        $('.pcoded-navigation-label').attr('menu-title-theme', 'theme6')
      }
    })
  }
  m()
  function i() {
    $('.theme-color > a.active-item-theme').on('click', function() {
      var w = $(this).attr('active-item-theme')
      $('.pcoded-navbar').attr('active-item-theme', w)
    })
  }
  i()
  function s() {
    $('.theme-color > a.sub-item-theme').on('click', function() {
      var w = $(this).attr('sub-item-theme')
      $('.pcoded-navbar').attr('sub-item-theme', w)
    })
  }
  s()
  function r() {
    $('.theme-color > a.themebg-pattern').on('click', function() {
      var w = $(this).attr('themebg-pattern')
      $('body').attr('themebg-pattern', w)
    })
  }
  r()
  function k() {
    $('#navigation-view')
      .val('view1')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded').attr('vnavigation-view', w)
      })
  }
  k()
  function q() {
    $('#theme-layout').change(function() {
      if ($(this).is(':checked')) {
        $('.pcoded').attr('vertical-layout', 'box')
        $('#bg-pattern-visiblity').removeClass('d-none')
      } else {
        $('.pcoded').attr('vertical-layout', 'wide')
        $('#bg-pattern-visiblity').addClass('d-none')
      }
    })
  }
  q()
  function j() {
    $('#vertical-menu-effect')
      .val('shrink')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded').attr('vertical-effect', w)
      })
  }
  j()
  function g() {
    $('#vertical-navbar-placement')
      .val('left')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded').attr('vertical-placement', w)
        $('.pcoded-navbar').attr('pcoded-navbar-position', 'absolute')
        $('.pcoded-header .pcoded-left-header').attr('pcoded-lheader-position', 'relative')
      })
  }
  g()
  function a() {
    $('#vertical-activeitem-style')
      .val('style1')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded-navbar').attr('active-item-style', w)
      })
  }
  a()
  function v() {
    $('#vertical-item-border').change(function() {
      if ($(this).is(':checked')) {
        $('.pcoded-navbar .pcoded-item').attr('item-border', 'false')
      } else {
        $('.pcoded-navbar .pcoded-item').attr('item-border', 'true')
      }
    })
  }
  v()
  function d() {
    $('#vertical-subitem-border').change(function() {
      if ($(this).is(':checked')) {
        $('.pcoded-navbar .pcoded-item').attr('subitem-border', 'false')
      } else {
        $('.pcoded-navbar .pcoded-item').attr('subitem-border', 'true')
      }
    })
  }
  d()
  function l() {
    $('#vertical-border-style')
      .val('solid')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded-navbar .pcoded-item').attr('item-border-style', w)
      })
  }
  l()
  function n() {
    $('#vertical-dropdown-icon')
      .val('style1')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded-navbar .pcoded-hasmenu').attr('dropdown-icon', w)
      })
  }
  n()
  function t() {
    $('#vertical-subitem-icon')
      .val('style5')
      .on('change', function(w) {
        w = $(this).val()
        $('.pcoded-navbar .pcoded-hasmenu').attr('subitem-icon', w)
      })
  }
  t()
  function f() {
    $('#sidebar-position').change(function() {
      if ($(this).is(':checked')) {
        $('.pcoded-navbar').attr('pcoded-navbar-position', 'fixed')
        $('.pcoded-header .pcoded-left-header').attr('pcoded-lheader-position', 'fixed')
      } else {
        $('.pcoded-navbar').attr('pcoded-navbar-position', 'absolute')
        $('.pcoded-header .pcoded-left-header').attr('pcoded-lheader-position', 'relative')
      }
    })
  }
  f()
  function u() {
    $('#header-position').change(function() {
      if ($(this).is(':checked')) {
        $('.pcoded-header').attr('pcoded-header-position', 'fixed')
        $('.pcoded-navbar').attr('pcoded-header-position', 'fixed')
        $('.pcoded-main-container').css('margin-top', $('.pcoded-header').outerHeight())
      } else {
        $('.pcoded-header').attr('pcoded-header-position', 'relative')
        $('.pcoded-navbar').attr('pcoded-header-position', 'relative')
        $('.pcoded-main-container').css('margin-top', '0px')
      }
    })
  }
  u()
  function h() {
    $('#collapse-left-header').change(function() {
      if ($(this).is(':checked')) {
        $('.pcoded-header, .pcoded ').removeClass('iscollapsed')
        $('.pcoded-header, .pcoded').addClass('nocollapsed')
      } else {
        $('.pcoded-header, .pcoded').addClass('iscollapsed')
        $('.pcoded-header, .pcoded').removeClass('nocollapsed')
      }
    })
  }
  h()
})
function handlemenutype(a) {
  $('.pcoded').attr('nav-type', a)
}
handlemenutype('st2')
